import { Button, Card, Flex, Image, Input, Table } from "antd";
import { useCallback, useEffect, useState } from "react";
import { Company as ICompany } from "@/interfaces/business";
import { useAppStore } from "@/hooks/useAppStore";
import { useTranslation } from "react-i18next";
import { CompanyDialog, useCompanyDialog } from "./CompanyDialog";
import { CommonService } from "@/app/services/common";

const { Search } = Input;

export const Company = () => {
    const { t } = useTranslation("Company");
    const { t: tCommon } = useTranslation("Common");

    const [dataSource, setDataSource] = useState<ICompany[]>([]);
    const [isLoading, setIsLoading] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");

    const { userInfo } = useAppStore((state) => state);

    const { openDialog } = useCompanyDialog();

    const fetchData = useCallback(async () => {
        setIsLoading(true);

        try {
            const listRes = await CommonService.getCompanyList({
                COMNAME: "",
                USERID: userInfo?.USERID,
            });
            setDataSource(listRes.RESULT_DATA);
        } catch (error) {
            console.log("Error getCompanyList: ", error);
        }

        setIsLoading(false);
    }, [userInfo]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const columns: any = [
        {
            title: t("COM_GRID_1_LOGOIMAGEURL"),
            dataIndex: "LOGOIMAGEURL",
            key: "LOGOIMAGEURL",
            width: 120,
            align: "center",
            render: (text: string) => (
                <Image
                    onClick={(e) => e.stopPropagation()}
                    preview={false}
                    width={60}
                    src={text}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: t("COM_GRID_1_BANNERIMAGEURL"),
            dataIndex: "BANNERIMAGEURL",
            key: "BANNERIMAGEURL",
            width: 120,
            align: "center",
            render: (text: string) => (
                <Image
                    onClick={(e) => e.stopPropagation()}
                    preview={false}
                    width={60}
                    src={text}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: t("COM_GRID_1_COMNAME"),
            dataIndex: "COMNAME",
            key: "COMNAME",
        },
        {
            title: t("COM_GRID_1_ZIP"),
            dataIndex: "ZIP",
            key: "ZIP",
        },
        {
            title: t("COM_GRID_1_ADDR"),
            dataIndex: "ADDR",
            key: "ADDR",
        },
        {
            title: t("COM_GRID_1_TEL"),
            dataIndex: "TEL",
            key: "TEL",
        },
        {
            title: t("COM_GRID_1_CEO"),
            dataIndex: "CEO",
            key: "CEO",
        },
        {
            title: t("COM_GRID_1_CHARGE"),
            dataIndex: "CHARGE",
            key: "CHARGE",
        },
        {
            title: t("COM_GRID_1_SECTOR"),
            dataIndex: "SECTOR",
            key: "SECTOR",
        },
        {
            title: t("COM_GRID_1_COMDESC"),
            dataIndex: "COMDESC",
            key: "COMDESC",
        },
        {
            title: t("COM_GRID_1_BANKINFO"),
            dataIndex: "BANKINFO",
            key: "BANKINFO",
        },
        {
            title: t("VAT"),
            dataIndex: "VAT",
            key: "VAT",
        },
        {
            title: t("COM_GRID_1_REVSHARE"),
            dataIndex: "REVSHARE",
            key: "REVSHARE",
        },
        {
            title: tCommon("CM_USEYN"),
            dataIndex: "USEYN",
            key: "USEYN",
            align: "center",
            render: (text: string) =>
                text == "Y" ? tCommon("CM_YN_Y") : tCommon("CM_YN_N"),
        },
        {
            title: t("COM_GRID_1_OUTBINDINGYN"),
            dataIndex: "OUTBINDINGYN",
            key: "OUTBINDINGYN",
            align: "center",
            render: (text: string) =>
                text == "Y" ? tCommon("CM_YN_Y") : tCommon("CM_YN_N"),
        },
    ];

    const handleAddItem = () => {
        console.log("handleAddItem");
        openDialog();
    };

    return (
        <Card
            title={t("COM_GRID_1_TITLE")}
            extra={
                <Flex gap="middle">
                    <Search
                        placeholder={t("COM_SEARCH_GRID_1_TITLE")}
                        allowClear
                        onSearch={(value) => {
                            setSearchTerm(value);
                        }}
                        style={{ width: 200 }}
                    />

                    <Button type="primary" onClick={handleAddItem}>
                        {tCommon("CM_ADD")}
                    </Button>
                </Flex>
            }
        >
            <Table
                rowKey="COMID"
                scroll={{ x: "max-content" }}
                loading={isLoading}
                dataSource={dataSource?.filter((item) =>
                    item.COMNAME.toLowerCase().includes(
                        searchTerm.toLowerCase()
                    )
                )}
                columns={columns}
                onRow={(record) => ({
                    onClick: () => openDialog(record),
                })}
            />
            <CompanyDialog
                onClose={async (status) => {
                    if (status === "success") {
                        await fetchData();
                    }
                }}
            />
        </Card>
    );
};
