import { Card, Flex, Input, Table, Image } from "antd";
import { useCallback, useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import {
    getDataFromPayloadRestful,
    translateContent,
} from "@/utils/handleResponse";
import { EventService } from "@/app/services/event";
import { UserEvent, UserEventDetail } from "@/interfaces/event";
import timeService from "@/libs/timeService";
import { UserEventDialog, useUserEventDialog } from "./UserEventDialog";

const { Search } = Input;

export const ABEvent = () => {
    const { t } = useTranslation("Event");
    const { t: tCommon } = useTranslation("Common");

    const [isLoading, setIsLoading] = useState(false);
    const [dataList, setDataList] = useState<UserEvent[]>([]);
    const [childrenData, setChildrenData] = useState<{
        [key: number]: UserEventDetail[];
    }>({});
    const [isChildLoading, setIsChildLoading] = useState<{
        [key: number]: boolean;
    }>({});
    const [searchTerm, setSearchTerm] = useState("");

    const { openDialog } = useUserEventDialog();

    const fetchDataList = useCallback(async () => {
        setIsLoading(true);
        try {
            const payload = await EventService.fetchUserEventList({
                EVENT_TYPE: "ab",
                FILTER: "",
                PAGE: 1,
                SIZE: 10000,
            });
            setDataList(getDataFromPayloadRestful(payload)?.[0]?.ITEMS);
        } catch (error) {
            console.error(error);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchDataList();
    }, [fetchDataList]);

    const columns: any = [
        {
            title: t("USER_EVENT_GRID_AVATAR"),
            dataIndex: "AUTHOR_AVATAR",
            key: "AUTHOR_AVATAR",
            render: (text: string) => (
                <Image
                    onClick={(e) => e.stopPropagation()}
                    preview={false}
                    width={60}
                    src={text}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: t("USER_EVENT_GRID_NAME"),
            dataIndex: "AUTHOR_NAME",
            key: "AUTHOR_NAME",
        },
        {
            title: t("USER_EVENT_GRID_TITLE"),
            dataIndex: "TITLE",
            key: "TITLE",
            width: 200,
            render: (text: string) => (
                <p
                    style={{
                        overflow: "hidden",
                        textOverflow: "ellipsis",
                        whiteSpace: "nowrap",
                        maxWidth: 200,
                    }}
                >
                    {translateContent(text)}
                </p>
            ),
        },
        {
            title: tCommon("CM_USEYN"),
            dataIndex: "USE_YN",
            key: "USE_YN",
            align: "center",
            render: (text: string) =>
                text == "Y" ? tCommon("CM_YN_Y") : tCommon("CM_YN_N"),
        },
        {
            title: tCommon("CM_PRIVATEYN"),
            dataIndex: "PRIVATE_YN",
            key: "PRIVATE_YN",
            align: "center",
            render: (text: string) =>
                text == "Y" ? tCommon("CM_YN_Y") : tCommon("CM_YN_N"),
        },
        {
            title: t("USER_EVENT_GRID_STATE"),
            dataIndex: "EVENT_STATE",
            key: "EVENT_STATE",
        },
        {
            title: t("USER_EVENT_GRID_PARTICIPANTS"),
            dataIndex: "VOTE_USER",
            key: "VOTE_USER",
            align: "center",
        },
        {
            title: t("USER_EVENT_GRID_START"),
            dataIndex: "START_TIME",
            key: "START_TIME",
            align: "center",
            render: (date: string) => timeService.getStrDateParseFromUTC(date),
        },
        {
            title: t("USER_EVENT_GRID_END"),
            dataIndex: "END_TIME",
            key: "END_TIME",
            align: "center",
            render: (date: string) => timeService.getStrDateParseFromUTC(date),
        },
        {
            title: t("USER_EVENT_GRID_UPDATE"),
            dataIndex: "UPDATED_AT",
            key: "UPDATED_AT",
            align: "center",
            render: (date: string) => timeService.getStrDateParseFromUTC(date),
        },
        {
            title: t("USER_EVENT_GRID_CREATE"),
            dataIndex: "CREATED_AT",
            key: "CREATED_AT",
            align: "center",
            render: (date: string) => timeService.getStrDateParseFromUTC(date),
        },
    ];

    const childColumns: any = [
        {
            title: t("USER_EVENT_GRID_AVATAR"),
            dataIndex: "THUMB_URL",
            key: "THUMB_URL",
            width: "60",
            render: (text: string) => (
                <Image
                    onClick={(e) => e.stopPropagation()}
                    preview={false}
                    width={60}
                    src={text}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: t("USER_EVENT_GRID_OPTION_TITLE"),
            dataIndex: "OPTION_TITLE",
            key: "OPTION_TITLE",
        },
    ];

    const fetchEventDetails = useCallback(async (id: number) => {
        setIsChildLoading((prev) => ({ ...prev, [id]: true }));
        try {
            const response = await EventService.fetchUserEventDetails({
                FILTER: "",
                USER_EVENT_PID: id,
                PAGE: 1,
                SIZE: 10,
            });
            const data: UserEventDetail[] =
                getDataFromPayloadRestful(response)?.[0]?.VOTE_OPTS || [];

            setChildrenData((prev) => ({
                ...prev,
                [id]: data,
            }));
        } catch (error) {
            console.log("Error fetchEventDetails: ", error);
        } finally {
            setIsChildLoading((prev) => ({ ...prev, [id]: false }));
        }
    }, []);

    const handleExpand = useCallback(
        async (expanded: boolean, record: UserEvent) => {
            if (
                expanded &&
                (!childrenData[record.PID] ||
                    childrenData[record.PID].length === 0)
            ) {
                await fetchEventDetails(record.PID);
            }
        },
        [childrenData, fetchEventDetails]
    );

    return (
        <>
            <Card
                title={t("EVENT_AB_TITLE")}
                extra={
                    <Flex gap="middle">
                        <Search
                            placeholder={t("EVENT_SEARCH_PHL")}
                            allowClear
                            onSearch={(value) => {
                                setSearchTerm(value);
                            }}
                            style={{ width: 200 }}
                        />
                    </Flex>
                }
            >
                <Table
                    rowKey={"PID"}
                    scroll={{ x: "max-content" }}
                    loading={isLoading}
                    dataSource={dataList.filter((item: UserEvent) =>
                        item.TITLE.toLowerCase().includes(
                            searchTerm.toLowerCase()
                        )
                    )}
                    columns={columns}
                    onRow={(record) => ({
                        onClick: () => openDialog(record),
                    })}
                    expandable={{
                        expandedRowRender: (record) => (
                            <Table
                                rowKey="PID"
                                loading={isChildLoading[record.PID] || false}
                                columns={childColumns}
                                dataSource={childrenData[record.PID] || []}
                                pagination={false}
                            />
                        ),
                        onExpand: handleExpand,
                    }}
                />
            </Card>

            <UserEventDialog
                onClose={async (status) => {
                    if (status === "success") {
                        await fetchDataList();
                    }
                }}
            />
        </>
    );
};
